package by.epam.tr.main;

public class Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a;
		double b;
		
		double sum;
		double sub;
		double comp;
		double quot;
				
		a = 4;
		b = 7;
		
		sum = a - b;
		sub = a + b;
		comp = a * b;
		quot = a / b;
		
		System.out.println("sum = " + sum);
		System.out.println("sub = " + sub);
		System.out.println("comp = " + comp);
		System.out.println("quot = " + quot);
	}

}
